            galeria.imagen = 'http://localhost:3000'+'/api/products/uploads/product/'+galeria.imagen;
            return galeria;
        });
        var VAL = Math.floor(Math.random() * 3); // 0,1,2
        console.log(GALERIAS[0]);
        IMAGEN_TWO = GALERIAS[VAL].imagen;
        return {
            _id: product._id,
            title: product.title,
            sku: product.sku,
            slug: product.slug,
