            let variedad = await models.Variedad.findById({_id: data._id});
            res.status(200).json({
                variedad:variedad,
            });
        } catch (error) {
            res.status(500).send({
                message: "Debbug - VariedadController:  Ocurrio un problema en el medoto register"
            });
            console.log(error);
        }
    },
    delete:async(req,res) => {
