            await models.Variedad.findByIdAndDelete({_id:_id});
            res.status(200).json({
                message:"La variedad se eleminó correctamente",
            });
        } catch (error) {
            res.status(500).send({
                message: "Debbug - VariedadController:  Ocurrio un problema en el medoto register"
            });
            console.log(error);
        }
    },
}

