            let cupone = await models.Cupone.findOne({_id: cupone_id});

            res.status(200).json({
                message: 200,
                cupon: cupone,
            });
        } catch (error) {
            res.status(500).send({
                message: "debbug: CuponeController show - OCURRIÓ UN PROBLEMA"
            });
            console.log(error);
        }
    },
    config:async(req, res) => {
        try {

